public class Problem_8 {
    public static void main(String[] args) {
        String bigNum = "";

        int indexOne = 0, indexTwo = 13;
        int first = Integer.parseInt(bigNum.substring(indexOne, indexOne + 1)), last = Integer.parseInt(bigNum.substring(indexTwo, indexTwo + 1));
        int biggest = 1;
        int current;

        while (indexTwo < bigNum.length()) {
            current = 1;
            for (int i = indexOne; i < indexTwo; i++) {
                current *= Integer.parseInt(bigNum.substring(i, i + 1));
            }
            indexOne++;
            indexTwo++;
            if (current > biggest) {
                biggest = current;
            }
        }
        System.out.println(biggest);
    }
}
